query GetRecommendedPosts($id: ID) {
    post(id: $id) {
      recommended_posts {
        id
        title
        short_description
        thumbnail
        likes
        user {
          id
          username
          profile {
            id
            thumbnail
            display_name
          }
        }
        url_slug
        released_at
        updated_at
        comments_count
        tags
        is_private
      }
    }
  }
