query ReadPostForEdit($id: ID) {
    post(id: $id) {
      id
      title
      tags
      body
      short_description
      is_markdown
      is_private
      is_temp
      thumbnail
      url_slug
      updated_at
      series {
        id
        name
      }
      user {
        id
      }
    }
  }
