query:
query ReloadComments($id: ID!) {
    post(id: $id) {
      id
      comments_count
      comments {
        id
        user {
          id
          username
          profile {
            id
            thumbnail
            display_name
          }
        }
        text
        replies_count
        level
        created_at
        deleted
      }
    }
  }
