query:
query SearchPosts($keyword: String!, $offset: Int, $username: String) {
    searchPosts(keyword: $keyword, offset: $offset, username: $username) {
      count
      posts {
        id
        title
        short_description
        thumbnail
        user {
          id
          username
          profile {
            id
            thumbnail
            display_name
          }
        }
        url_slug
        released_at
        tags
        is_private
        comments_count
      }
    }
  }
