query:
mutation EditSeries($id: ID!, $name: String!, $series_order: [ID]) {
    editSeries(id: $id, name: $name, series_order: $series_order) {
      id
      name
      user {
        id
        username
        profile {
          id
          display_name
          short_bio
          thumbnail
          profile_links
        }
      }
      series_posts {
        id
        index
        post {
          id
          title
          thumbnail
          short_description
          url_slug
          released_at
        }
      }
    }
  }
